//Fonction de création des graphiques
const plot = function(xs,ys,tit,unity,pos,graph_type,a,b,xvals,donnees) {

  couples = zip(xs, ys, (x, y) => ({ x, y }))
  console.log(couples)

    var chart = new CanvasJS.Chart(pos,
      {
        zoomEnabled:true,
        animationEnabled: true,
        exportEnabled: true,
        backgroundColor: "#EAF9BF",
        title:{
          text: tit,
        },
        axisX: {
  		    valueFormatString: "DD-MM-YYYY",
  	    },
        axisY:{
          title: unity
        },
        data: [{ //Affichage des points de la courbe
            color: 'blue',
            type: graph_type, //Affichage d'une courbe
            fillOpacity: .4,
            dataPoints: couples,
        },
        {
        color: 'red',
        type:"line",
        dataPoints: [ //Affichage des 2 points de la régression linéaire
          { x: new Date (2009,00,01), y: b },
          { x: new Date (2020,03,16), y: a*donnees.length+b }
        ]
        }
        ]
      }
    );

    chart.render();
}

//Fonction qui parse le fichier de corresondances lors du chargement de la page 
const parse_corres=function(){
    Papa.parse('https://raw.githubusercontent.com/clementyziquel/clementyziquel.github.io/master/data/correspondance.csv',{
      delimiter: ";",
      header: true,
      download: true,
      complete: function(results, file) {
        console.log(results, file);
        corres=results.data;
  
        let numero = [];
        for(i=0;i<corres.length;i++) {
          numero[i] = corres[i].numero;
        }
        
        let ville = [];
        for(i=0;i<corres.length;i++) {
          ville[i] = corres[i].ville;
        }    
      }
    }
    )  
}
let corres = [];
window.onload= parse_corres;

const name_modif = function(ville){
  ville=ville.replace(/ \([0-9]\)/g, ''); //je retire les (nombre) s'il y en a
  ville = ville.substring(0, ville.length - 4); //Je retire le .csv en fin de chaine
  ville = ville.replace(/export-/g,'');
  ville = ville.replace(/_/g, ' '); //Je mets un espace à la place du _ dans le nom de la ville
  ville = ville[0].toUpperCase() + ville.slice(1);//Mettre la première lettre de la chaine en majuscule
  return ville;
}

const downloadville = function() {
  let vil = document.getElementById("myInput").value;
  idvil=ville2num(vil,corres);

  if(idvil!=""){
    /* téléchargement du fichier */  
    let nurl="https://www.historique-meteo.net/site/export.php?ville_id=" + idvil;
    /* creation d une balise <a></a> */
    var link = document.createElement('a');
    /* ajout de l'attibut href */
    var link = document.createElement('a');
    link.href = nurl;
    link.dispatchEvent(new MouseEvent('click'));
    document.getElementById("erreur").innerHTML = "";
  }else{
    document.getElementById("erreur").innerHTML = "Ville non existante"; //Cas où l'utilisateur entre une ville inexistante dans les données
  }
  document.body.childNodes[8].childNodes[1].childNodes[1].value="";//Modification du css pour faire disparaître le nom de la ville dans le input une fois que l'utilisateur clique sur télécharger
  document.body.childNodes[8].childNodes[1].childNodes[1].placeholder="Entrez une autre ville";//Modification du css De l'affichage par défaut du champ d'input
}

const zip = function(ar1, ar2, zipper) {
    return zipper
    ? ar1.map((value, index) => zipper(value, ar2[index]))
    : ar1.map((value, index) => [value, ar2[index]])
    ;
}

//Fonction qui calcule et affiche les prévisions pour 2050
const previsions = function(a,b,ville,compteur,parametre,unité){ 
    jours= 365*30; //C'est le nombre de jours approximatif avant 2050
    valeur= a*jours+b;
    valeur2009=Math.round((a+b)*1000)/1000;
    valeur2020=Math.round((a*4019+b)*1000)/1000;
    valeur2050=Math.round(valeur*1000)/1000;
    document.getElementById("tendance"+compteur).innerHTML = "Si la tendance actuelle se poursuit, à "+ville+" "+parametre+" moyenne sera de <b>"+valeur2050+unité+" en 2050</b> contre "+valeur2009+unité+" en 2009 et "
                    +valeur2020+unité+" en 2020.";
}
  
//Fonction d'analyse des données de température issues du calcul de la régression linéaire
const affiche_analyse_temp = function(aMin,aMax,indice){
    if(aMin>0 && aMax>0){
      message="Le réchauffement climatique est particulièrement visible dans cette ville. En effet, les températures moyennes minimales et maximales augmentent, cette ville est donc une preuve concrète du réchauffement climatique.";
    }else if(aMin<0 && aMax>0){
      message="Le réchauffement climatique est particulièrement visible dans cette ville. En effet, la température minimale a tendance à diminuer tandis que la température maximale augmente, les variations deviennent de plus en plus importantes.";
    }else if(aMin>0 && aMax<0){
      message="Le réchauffement climatique est peu visible dans cette ville. En effet, la température minimale a tendance à augmenter tandis que la température maximale diminue.";
    }else if(aMin<0 && aMax<0){
      message="Le réchauffement climatique n'est pas visible dans cette ville. En effet, les tempértures moyennes minimales et maximales diminuent.";
    }else if(aMin==0 || aMax==0){
      message="Le réchauffement climatique n'est pas visible dans cette ville. En effet, les tempértures moyennes minimales et maximales semblent constantes.";
    }
    document.getElementById("affiche_analyse_temp"+indice).innerHTML = message;
}

//Fonction d'analyse des données de précipitations issues du calcul de la régression linéaire
const affiche_analyse_precip = function(a,indice){
    if(a>0){
      message="Il y a de plus en plus de pluie dans cette ville, c'est donc un signe de dérèglement climatique.";
    }else if(a<0){
      message="Il y a de moins en moins de pluie dans cette ville, c'est donc un signe de dérèglement climatique.";
    }
    document.getElementById("affiche_analyse_precip"+indice).innerHTML = message;
}
  
//Fonction qui fait la correcpondance entre le nom de la ville et son id afin de 
//ne télécharger que le fichier nécessaire
const ville2num = function(vilstr,corres){
    if(vilstr=="") return "";
    for(i=0;i<7377;i++){
      if(vilstr==corres[i].ville){
        return corres[i].numero;
      }
    }
    return "";
}

